import React, { useState } from "react";
import "../styles/CadastroCliente.css";

const CadastroCliente = ({ onClienteCadastrado }) => {
  const [formData, setFormData] = useState({
    nome: "",
    email: "",
    dataNascimento: "",
    contato: "",
    endereco: { pais: "", estado: "", cidade: "" },
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (["pais", "estado", "cidade"].includes(name)) {
      setFormData((prev) => ({
        ...prev,
        endereco: { ...prev.endereco, [name]: value },
      }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async () => {
    try {
      const response = await fetch("http://localhost:3001/clientes", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ...formData, id: Date.now() }),
      });

      if (response.ok) {
        const novoCliente = await response.json();
        onClienteCadastrado(novoCliente); // Notifica o componente pai sobre o novo cliente
        alert("Cliente cadastrado com sucesso!");
        setFormData({
          nome: "",
          email: "",
          dataNascimento: "",
          contato: "",
          endereco: { pais: "", estado: "", cidade: "" },
        });
      } else {
        alert("Erro ao cadastrar cliente.");
      }
    } catch (error) {
      console.error("Erro ao conectar ao servidor:", error);
      alert("Erro ao cadastrar cliente.");
    }
  };

  return (
    <div className="cadastro-cliente">
      <h1>Cadastro de Cliente</h1>
      <form>
        <input
          type="text"
          name="nome"
          placeholder="Nome"
          value={formData.nome}
          onChange={handleChange}
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
        />
        <div>
          <input
            type="date"
            name="dataNascimento"
            placeholder="Data de Nascimento"
            value={formData.dataNascimento}
            onChange={handleChange}
          />
          <input
            type="text"
            name="contato"
            placeholder="Contato"
            value={formData.contato}
            onChange={handleChange}
          />
        </div>
        <h3>Endereço</h3>
        <div>
          <input
            type="text"
            name="pais"
            placeholder="País"
            value={formData.endereco.pais}
            onChange={handleChange}
          />
          <input
            type="text"
            name="estado"
            placeholder="Estado"
            value={formData.endereco.estado}
            onChange={handleChange}
          />
          <input
            type="text"
            name="cidade"
            placeholder="Cidade"
            value={formData.endereco.cidade}
            onChange={handleChange}
          />
        </div>
        <button type="button" onClick={handleSubmit}>
          Cadastrar
        </button>
      </form>
    </div>
  );
};

export default CadastroCliente;
