import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "../styles/DetalhesProprietario.css";

const DetalhesProprietario = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [proprietario, setProprietario] = useState(null);

  useEffect(() => {
    const fetchProprietario = async () => {
      try {
        const response = await fetch(`http://localhost:5000/proprietarios/${id}`);
        const data = await response.json();
        setProprietario(data);
      } catch (error) {
        console.error("Erro ao buscar proprietário:", error);
      }
    };

    fetchProprietario();
  }, [id]);

  if (!proprietario) {
    return <p>Proprietário não encontrado!</p>;
  }

  return (
    <div className="visualizar-proprietarios">
      <header className="header">
        <button className="back-button" onClick={() => navigate(-1)}>
          &larr;
        </button>
        <h1>Proprietário</h1>
      </header>
      <div className="proprietario-detalhes">
        <div className="dados">
          <p><strong>Nome:</strong> {proprietario.nome}</p>
          <p><strong>Telefone:</strong> {proprietario.telefone}</p>
          <p><strong>Tipo de Imóvel:</strong> {proprietario.tipoImovel}</p>
          <p><strong>Descrição dos Imóveis:</strong> {proprietario.descricaoImovel}</p>
          <h2>Localização dos Imóveis:</h2>
        </div>
        <div className="localizacao">
          <p><strong>País:</strong> {proprietario.localizacao.pais}</p>
          <p><strong>Estado:</strong> {proprietario.localizacao.estado}</p>
          <p><strong>Cidade:</strong> {proprietario.localizacao.cidade}</p>
        </div>
      </div>
    </div>
  );
};

export default DetalhesProprietario;
