import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import CadastroCliente from "./CadastroCliente";
import "../styles/VisualizarClientes.css";

const VisualizarClientes = () => {
  const navigate = useNavigate();
  const [clientes, setClientes] = useState([]);
  const [exibirCadastro, setExibirCadastro] = useState(false);

  useEffect(() => {
    const fetchClientes = async () => {
      try {
        const response = await fetch("http://localhost:3001/clientes");
        const data = await response.json();
        setClientes(data);
      } catch (error) {
        console.error("Erro ao buscar clientes:", error);
      }
    };

    fetchClientes();
  }, []);

  const handleCadastrarCliente = () => {
    setExibirCadastro(true);
  };

  const handleClienteCadastrado = (novoCliente) => {
    setClientes((prevClientes) => [...prevClientes, novoCliente]);
    setExibirCadastro(false); // Volta para a lista após o cadastro
  };

  const handleClienteClick = (clienteId) => {
    navigate(`/cliente/${clienteId}`);
  };

  return (
    <div className="visualizar-clientes">
      {exibirCadastro ? (
        <CadastroCliente onClienteCadastrado={handleClienteCadastrado} />
      ) : (
        <>
          <header className="header">
            <h1>Visualização de Clientes</h1>
          </header>
          <div className="clientes-lista">
            {clientes.map((cliente) => (
              <div
                key={cliente.id}
                className="cliente-card"
                onClick={() => handleClienteClick(cliente.id)}
              >
                <p>
                  <strong>Nome</strong>: {cliente.nome}
                </p>
                <p>
                  <strong>Data de Nascimento</strong>: {cliente.dataNascimento}
                </p>
              </div>
            ))}
            <div className="action">
              <button className="btn-primary" onClick={handleCadastrarCliente}>
                Cadastrar Cliente
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default VisualizarClientes;
