const CadastroImovelAlugar = () => {
  const [showMessage, setShowMessage] = useState(false);
  const [formData, setFormData] = useState({
    tipo: "",
    area: "",
    comodos: "",
    endereco: { pais: "", estado: "", cidade: "" }
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (["pais", "estado", "cidade"].includes(name)) {
      setFormData((prev) => ({
        ...prev,
        endereco: { ...prev.endereco, [name]: value }
      }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleCadastrar = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch("http://localhost:5000/imoveisAlugar", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData)
      });
      if (response.ok) {
        setShowMessage(true);
        setTimeout(() => setShowMessage(false), 3000);
      }
    } catch (error) {
      console.error("Erro ao cadastrar imóvel:", error);
    }
  };

  return (
    <form onSubmit={handleCadastrar}>
      <input name="tipo" type="text" placeholder="Tipo do Imóvel" onChange={handleInputChange} required />
      <input name="area" type="text" placeholder="Área do Imóvel" onChange={handleInputChange} required />
      <input name="comodos" type="number" placeholder="Quantidade de Cômodos" onChange={handleInputChange} required />
      <input name="pais" type="text" placeholder="País" onChange={handleInputChange} required />
      <input name="estado" type="text" placeholder="Estado" onChange={handleInputChange} required />
      <input name="cidade" type="text" placeholder="Cidade" onChange={handleInputChange} required />
      <button type="submit">Cadastrar Imóvel</button>
    </form>
  );
};
