import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/Home.css";

const Home = () => {
  const [imoveis, setImoveis] = useState([]); // Todos os imóveis
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Obtém os dados de venda e aluguel
        const [vendaRes, aluguelRes] = await Promise.all([
          fetch("http://localhost:5000/venda"),
          fetch("http://localhost:5000/aluguel"),
        ]);
        const vendaData = await vendaRes.json();
        const aluguelData = await aluguelRes.json();

        // Combina os dados, adicionando uma chave para diferenciar
        const allProperties = [
          ...vendaData.map((imovel) => ({ ...imovel, tipoNegocio: "venda" })),
          ...aluguelData.map((imovel) => ({ ...imovel, tipoNegocio: "aluguel" })),
        ];

        setImoveis(allProperties); // Armazena todos os imóveis juntos
      } catch (error) {
        console.error("Erro ao buscar imóveis:", error);
      }
    };
    fetchData();
  }, []);

  const handleDetalhes = (id, tipoNegocio) => {
    navigate(`/detalhes-imovel/${tipoNegocio}/${id}`);
  };

  return (
    <div className="home">
      <div className="banner">
        <h1>O seu sonho a um clique de distância!</h1>
        <input
          type="text"
          placeholder="Digite a cidade ou estado"
          className="search-bar"
        />
        <button className="search-button">
          <i className="fas fa-search"></i>
        </button>
      </div>

      <div className="properties">
        {imoveis.map((imovel) => (
          <div key={imovel.id} className="property-card">
            <div className="property-info">
              <p><strong>Tipo:</strong> {imovel.tipo}</p>
              <p><strong>Cômodos:</strong> {imovel.comodos}</p>
              <p><strong>Área:</strong> {imovel.area} m²</p>
              <p><strong>Endereço:</strong> {imovel.endereco.cidade} - {imovel.endereco.estado} - {imovel.endereco.pais}</p>
            </div>
            <div className="property-actions">
              {imovel.tipoNegocio === "venda" && (
                <button
                  className="btn-sale"
                  onClick={() => handleDetalhes(imovel.id, "venda")}
                >
                  À VENDA
                </button>
              )}
              {imovel.tipoNegocio === "aluguel" && (
                <button
                  className="btn-rent"
                  onClick={() => handleDetalhes(imovel.id, "aluguel")}
                >
                  À ALUGAR
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
