import React, { useState } from "react";
import { useParams } from "react-router-dom";
import "../styles/VisualizarCliente.css";

const VisualizarCliente = () => {
  const { id } = useParams(); // ID do cliente da URL
  const [cliente] = useState({
    nome: id === "1" ? "João Silva" : "Antonio Araújo",
    dataNascimento: id === "1" ? "01/01/2000" : "30/05/2003",
    email: id === "1" ? "joao@email.com" : "antonio@email.com",
    contato: id === "1" ? "123456789" : "987654321",
  });

  return (
    <div className="visualizar-cliente">
      <h2>Detalhes do Cliente</h2>
      <p><strong>Nome:</strong> {cliente.nome}</p>
      <p><strong>Email:</strong> {cliente.email}</p>
      <p><strong>Data de Nascimento:</strong> {cliente.dataNascimento}</p>
      <p><strong>Contato:</strong> {cliente.contato}</p>
      <button
        onClick={() => alert("Funcionalidade de edição simulada!")}
        className="botao-editar"
      >
        Editar Dados
      </button>
    </div>
  );
};

export default VisualizarCliente;
